      			c7.setCellValue(TimeResponse);

                     log.info "Request: "+xmlString+ "\n";
               	 log.info "Response: "+res+ "\n";
               	 log.info "Resultado:"+result+"\n";

               	 }
               	 catch(Exception expObj)
               	 {
               	 	//  Exception Handler
               	def res1 = context.expand('${DT-SER-26#Response}');
